import { Component, OnInit } from '@angular/core';
import { Country } from '../model/country';
import { CountryService } from '../service/country-service';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-show-country',
  templateUrl: './show-country.component.html',
  styleUrls: ['./show-country.component.css']
})
export class ShowCountryComponent implements OnInit {

  countryForm: FormGroup;
  country: Country = new Country()
  countryID: number;
  countryName: string = '';
  countryLanguage: string = '';

  constructor(private countryService: CountryService, private route: ActivatedRoute, private router: Router, private fb: FormBuilder) {
    const idParam = this.route.snapshot.paramMap.get('id');
    if (idParam !== null) {
      this.countryID = parseInt(idParam);
    } else {
      this.countryID = -1;
    }  

    this.countryForm = this.fb.group({
      id: '',
      name: '',
      language: ''
    });
  }

  onSubmit() {


    if (this.countryForm.valid) {
      const Counts = {
        id: this.countryID,
        name: this.countryName,
        language: this.countryLanguage
      };

      this.countryService.updateCountry(this.countryID, Counts ).subscribe(
        (response) => {
          // Handle successful response
          console.log('Country created successfully:', response);
          //this.updateForm.reset(); // Reset the form
          this.router.navigate(['/countries']);
        },
        (error) => {
          // Handle error
          console.error('Error creating country:', error);
        }
      );
    }
  }  

  ngOnInit(): void {
    this.route.params.forEach((params: Params) => {
      if (params['id'] !== undefined) {
        const id = params['id'];
        this.countryService.getCountry(id).subscribe(data => { this.country = data });
      }
    });
  }

  executeDeleteFunction(): void {
    this.route.params.forEach((params: Params) => {
      if (params['id'] !== undefined) {
        const id = params['id'];
        this.countryService.deleteCountry(id).subscribe(data => { this.country = data });
        this.router.navigate(['/countries']);
      }

    });
  }


  }
